import { Component, OnInit } from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
import { ArtworkService } from '../../services/artwork.service';
import { ArtistService } from '../../services/artist.service';
import { ArtworkListItem } from '../../models/artwork.model';
import { ArtistListItem } from '../../models/artist.model';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  featuredArtworks: ArtworkListItem[] = [];
  featuredArtists: ArtistListItem[] = [];
  loading = false;
  error: string | null = null;

  constructor(
    private artworkService: ArtworkService,
    private artistService: ArtistService,
    private translate: TranslateService
  ) { }

  ngOnInit(): void {
    this.loadFeaturedContent();
  }

  private loadFeaturedContent(): void {
    this.loading = true;
    
    // Load featured artworks
    this.artworkService.getArtworks()
      .subscribe(
        artworks => {
          // Get the most recent artworks (up to 6)
          this.featuredArtworks = artworks
            .sort((a, b) => b.creationYear - a.creationYear)
            .slice(0, 6);
          this.loading = false;
        },
        error => {
          this.error = 'Failed to load featured artworks';
          this.loading = false;
        }
      );
    
    // Load featured artists
    this.artistService.getArtists()
      .subscribe(
        artists => {
          // Get a random selection of artists (up to 4)
          this.featuredArtists = this.getRandomItems(artists, 4);
        },
        error => {
          this.error = 'Failed to load featured artists';
        }
      );
  }

  private getRandomItems<T>(items: T[], count: number): T[] {
    const shuffled = [...items].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
  }
}
